import React, {useState, useEffect, useCallback} from 'react';
import './App.css';
import axios from 'axios';
import {useDropzone} from 'react-dropzone'

const CreateUserProfiles = ({forceUpdateCallback}) => {
  const [userField, setUserField] = useState("");

  const handleSubmit = () => {
    const formData = new FormData();
    formData.set("username", userField);

    axios.post(
      `http://localhost:8080/api/v1/user-profile/create`,
      formData,
      {
        headers: {
          "Content-Type": "application/json"
        }
      }
    ).then(() => {
      console.log("user created successfully");
    }).catch(err => {
      console.log(err);
    });

    setUserField("");
    forceUpdateCallback();
  }

  return (
    <div>
      <input type="text" value={userField} onChange={(event) => setUserField(event.target.value)}/>
      <div class="button">
        <div id="underline"></div>
        <a onClick={handleSubmit}>Add</a>
      </div>
    </div>
  );
}

const UserProfiles = () => {
  const [userProfiles, setUserProfiles] = useState([]);

  const fetchUserProfiles = () => {
    axios.get("http://localhost:8080/api/v1/user-profile").then(res => {
      console.log(res);
      setUserProfiles(res.data);
    });
  };

  useEffect(() => {
    fetchUserProfiles();
  });

  const forceUpdate = () => {
    setUserProfiles([]);
  }

  return (
    <>
    <CreateUserProfiles forceUpdateCallback={forceUpdate}/>
    <div>
      {userProfiles.map((userProfile, index) => {
        return (
          <div key={index}>
            {userProfile.userProfileId ? (
              <img src={`http://localhost:8080/api/v1/user-profile/${userProfile.userProfileId}/image/download`} alt=""/>
            ) : null}
            <br/>
            <br/>
            <h1>{userProfile.username}</h1>
            <p>{userProfile.userProfileId}</p>
            <Dropzone {...userProfile} forceUpdateCallback={forceUpdate}/>
            <br/>
          </div>
        )
      })}
    </div>
    </>
  );
}

function Dropzone({ userProfileId, forceUpdateCallback }) {
  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];
    console.log(file);
    const formData = new FormData();
    formData.append("file", file);

    axios.post(
      `http://localhost:8080/api/v1/user-profile/${userProfileId}/image/upload`,
      formData,
      {
        headers: {
          "Content-Type": "multipart/form-data"
        }
      }
    ).then(() => {
      console.log("file uploaded successfully");
    }).catch(err => {
      console.log(err);
    });

    //force update
    forceUpdateCallback();

  }, [])
  const {getRootProps, getInputProps, isDragActive} = useDropzone({onDrop})

  return (
    <div {...getRootProps()}>
      <input {...getInputProps()} />
      {
        isDragActive ?
          <p>Drop the image here ...</p> :
          <p>Drag 'n' drop profile image here, or click to select image</p>
      }
    </div>
  )
}

function App() {
  return (
    <div className="App">
      <UserProfiles/>
    </div>
  );
}

export default App;
